import React, { useState } from 'react';
import { Check, X, Star, Users, Award, Zap, Shield } from 'lucide-react';
import { pricingPlans } from '../data/mockData';
import toast from 'react-hot-toast';
import api from '../services/api';

const PricingPage = () => {
  const [billingCycle, setBillingCycle] = useState('month');
  const [selectedPlan, setSelectedPlan] = useState(null);

  const handleSelectPlan = async (planId) => {
    try {
      setSelectedPlan(planId);

      if (planId === 'free') {
        toast.success('უფასო გეგმა არჩეულია!');
        return;
      }

      // For premium plans, create subscription
      const response = await api.createSubscription({
        planId,
        billingCycle: billingCycle
      });

      if (response.success) {
        toast.success(`გეგმა "${planId}" წარმატებით არჩეულია!`);
        // You could redirect to a success page or dashboard here
      }
    } catch (error) {
      console.error('Subscription error:', error);
      toast.error('გეგმის არჩევისას შეცდომა მოხდა');
    }
  };

  const getPlanPrice = (plan) => {
    if (plan.price === 0) return 'უფასო';
    const price = billingCycle === 'year' ? plan.price * 10 : plan.price; // 2 months free for yearly
    return `₾${price}/${billingCycle === 'year' ? 'წელი' : 'თვე'}`;
  };

  const getYearlySavings = (plan) => {
    if (plan.price === 0) return null;
    const monthlyPrice = plan.price;
    const yearlyPrice = monthlyPrice * 10; // 2 months free
    const savings = (monthlyPrice * 12) - yearlyPrice;
    return `დაზოგეთ ₾${savings}/წელი`;
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4 font-display">
            აირჩიეთ თქვენი გეგმა
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            დაიწყეთ მასწავლებლობა უფასოდ ან გადადით უფრო მაღალ დონეზე პრემიუმი ფუნქციების გასახსნელად და მეტი სტუდენტის მისაღწევად
          </p>
        </div>

        {/* Billing Toggle */}
        <div className="flex justify-center mb-8">
          <div className="bg-white rounded-lg p-1 border border-gray-200">
            <button
              onClick={() => setBillingCycle('month')}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                billingCycle === 'month'
                  ? 'bg-primary-600 text-white'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              ყოველთვიური
            </button>
            <button
              onClick={() => setBillingCycle('year')}
              className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                billingCycle === 'year'
                  ? 'bg-primary-600 text-white'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              წლიური
              <span className="ml-1 bg-accent-500 text-white text-xs px-2 py-0.5 rounded-full">
                დაზოგეთ 17%
              </span>
            </button>
          </div>
        </div>

        {/* Pricing Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-12">
          {pricingPlans.map((plan) => (
            <div
              key={plan.id}
              className={`card relative ${
                plan.popular ? 'ring-2 ring-primary-500' : ''
              }`}
            >
              {plan.popular && (
                <div className="absolute -top-4 left-1/2 transform -translate-x-1/2">
                  <span className="bg-primary-600 text-white px-3 py-1 rounded-full text-xs font-medium whitespace-nowrap">
                    პოპულარული
                  </span>
                </div>
              )}

              <div className="text-center mb-6">
                <h3 className="text-2xl font-bold text-gray-900 mb-2 font-display">{plan.name}</h3>
                <div className="mb-4">
                  <span className="text-4xl font-bold text-gray-900">
                    {getPlanPrice(plan)}
                  </span>
                  {plan.price > 0 && billingCycle === 'year' && (
                    <p className="text-sm text-accent-600 font-medium mt-1">
                      {getYearlySavings(plan)}
                    </p>
                  )}
                </div>
                <p className="text-gray-600">
                  {plan.price === 0 ? 'იდეალური დასაწყებად' : 'ყველაფერი რაც გჭირდებათ ზრდისთვის'}
                </p>
              </div>

              {/* Features */}
              <div className="space-y-4 mb-8">
                {plan.features.map((feature, index) => (
                  <div key={index} className="flex items-start">
                    <Check className="w-5 h-5 text-green-500 mr-3 mt-0.5 flex-shrink-0" />
                    <span className="text-gray-700">{feature}</span>
                  </div>
                ))}
                {plan.limitations.map((limitation, index) => (
                  <div key={index} className="flex items-start">
                    <X className="w-5 h-5 text-red-500 mr-3 mt-0.5 flex-shrink-0" />
                    <span className="text-gray-500">{limitation}</span>
                  </div>
                ))}
              </div>

              {/* CTA Button */}
              <button
                onClick={() => handleSelectPlan(plan.id)}
                className={`w-full py-3 px-4 rounded-lg font-medium hover-lift ${
                  plan.popular
                    ? 'btn-primary'
                    : plan.price === 0
                    ? 'btn-secondary'
                    : 'btn-accent'
                }`}
              >
                {plan.price === 0 ? 'უფასოდ დაწყება' : 'გეგმის არჩევა'}
              </button>
            </div>
          ))}
        </div>

        {/* Features Comparison */}
        <div className="card mb-12">
          <h2 className="text-2xl font-bold text-gray-900 mb-8 text-center font-display">
            ყველა ფუნქციის შედარება
          </h2>
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className="border-b border-gray-200">
                  <th className="text-left py-4 px-6 font-medium text-gray-900">ფუნქცია</th>
                  <th className="text-center py-4 px-6 font-medium text-gray-900">უფასო</th>
                  <th className="text-center py-4 px-6 font-medium text-gray-900">პრემიუმი</th>
                  <th className="text-center py-4 px-6 font-medium text-gray-900">პროფესიონალი</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                <tr>
                  <td className="py-4 px-6 text-gray-700">პროფილის ჩამონათვალი</td>
                  <td className="text-center py-4 px-6">
                    <Check className="w-5 h-5 text-green-500 mx-auto" />
                  </td>
                  <td className="text-center py-4 px-6">
                    <Check className="w-5 h-5 text-green-500 mx-auto" />
                  </td>
                  <td className="text-center py-4 px-6">
                    <Check className="w-5 h-5 text-green-500 mx-auto" />
                  </td>
                </tr>
                <tr>
                  <td className="py-4 px-6 text-gray-700">სტუდენტთა კავშირები</td>
                  <td className="text-center py-4 px-6 text-gray-500">5-მდე</td>
                  <td className="text-center py-4 px-6 text-green-600 font-medium">ულიმიტო</td>
                  <td className="text-center py-4 px-6 text-green-600 font-medium">ულიმიტო</td>
