import React, { useState } from 'react';
import { Check, Crown, Star } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import { pricingPlans } from '../data/mockData';
import api from '../services/api';
import toast from 'react-hot-toast';

const PricingPage = () => {
  const [billingCycle, setBillingCycle] = useState('monthly');
  const [loading, setLoading] = useState(false);
  const { currentUser } = useAuth();

  const handleSubscribe = async (planId) => {
    if (!currentUser) {
      toast.error('გთხოვთ შეხვიდეთ სისტემაში');
      return;
    }

    setLoading(true);
    try {
      await api.createSubscription({
        planId,
        billingCycle,
        userId: currentUser.id
      });
      toast.success('გამოწერა წარმატებულია!');
    } catch (error) {
      toast.error(error.message || 'გამოწერის შეცდომა');
    } finally {
      setLoading(false);
    }
  };

  const getPrice = (plan) => {
    return billingCycle === 'monthly' ? plan.monthlyPrice : plan.yearlyPrice;
  };

  const getSavings = (plan) => {
    if (billingCycle === 'yearly' && plan.yearlySavings) {
      return plan.yearlySavings;
    }
    return null;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-purple-50 pt-20 pb-16">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-16">
          <h1 className="font-display text-4xl md:text-6xl font-bold text-gray-900 mb-6">
            აირჩიეთ თქვენი გეგმა
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto mb-8">
            აირჩიეთ იდეალური გეგმა თქვენი საჭიროებების მიხედვით
          </p>

          {/* Billing Toggle */}
          <div className="flex items-center justify-center space-x-4">
            <span className={`text-sm font-medium ${billingCycle === 'monthly' ? 'text-gray-900' : 'text-gray-500'}`}>
              ყოველთვიური
            </span>
            <button
              onClick={() => setBillingCycle(billingCycle === 'monthly' ? 'yearly' : 'monthly')}
              className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${
                billingCycle === 'yearly' ? 'bg-indigo-600' : 'bg-gray-200'
              }`}
            >
              <span
                className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                  billingCycle === 'yearly' ? 'translate-x-6' : 'translate-x-1'
                }`}
              />
            </button>
            <span className={`text-sm font-medium ${billingCycle === 'yearly' ? 'text-gray-900' : 'text-gray-500'}`}>
              წლიური
            </span>
            {billingCycle === 'yearly' && (
              <span className="bg-green-100 text-green-800 text-xs font-medium px-2 py-1 rounded-full">
                დაზოგეთ 20%
              </span>
            )}
          </div>
        </div>

        {/* Pricing Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {pricingPlans.map((plan) => (
            <div
              key={plan.id}
              className={`relative card-hover p-8 ${
                plan.featured ? 'ring-2 ring-indigo-500 shadow-strong' : ''
              }`}
            >
              {/* Featured Badge */}
              {plan.featured && (
                <div className="absolute -top-4 left-1/2 transform -translate-x-1/2">
                  <div className="bg-gradient-to-r from-indigo-500 to-purple-600 text-white px-4 py-2 rounded-full text-sm font-semibold whitespace-nowrap flex items-center">
                    <Crown className="w-4 h-4 mr-1" />
                    პოპულარული
                  </div>
                </div>
              )}

              {/* Plan Header */}
              <div className="text-center mb-8">
                <div className="flex items-center justify-center mb-4">
                  {plan.icon === 'star' && <Star className="w-8 h-8 text-yellow-500" />}
                  {plan.icon === 'crown' && <Crown className="w-8 h-8 text-purple-500" />}
                </div>
                <h3 className="font-display text-2xl font-bold text-gray-900 mb-2">
                  {plan.name}
                </h3>
                <p className="text-gray-600 mb-6">
                  {plan.description}
                </p>
                <div className="mb-6">
                  <span className="text-4xl font-bold text-gray-900">
                    ₾{getPrice(plan)}
                  </span>
                  <span className="text-gray-500 ml-2">
                    /{billingCycle === 'monthly' ? 'თვე' : 'წელი'}
                  </span>
                  {getSavings(plan) && (
                    <div className="text-sm text-green-600 font-medium mt-1">
                      დაზოგეთ ₾{getSavings(plan)}
                    </div>
                  )}
                </div>
              </div>

              {/* Features */}
              <div className="space-y-4 mb-8">
                {plan.features.map((feature, index) => (
                  <div key={index} className="flex items-start">
                    <Check className="w-5 h-5 text-green-500 mr-3 mt-0.5 flex-shrink-0" />
                    <span className="text-gray-700">{feature}</span>
                  </div>
                ))}
                {plan.limitations && plan.limitations.map((limitation, index) => (
                  <div key={index} className="flex items-start">
                    <div className="w-5 h-5 mr-3 mt-0.5 flex-shrink-0 text-gray-400">•</div>
                    <span className="text-gray-500">{limitation}</span>
                  </div>
                ))}
              </div>

              {/* CTA Button */}
              <button
                onClick={() => handleSubscribe(plan.id)}
                disabled={loading}
                className={`w-full py-3 px-6 rounded-xl font-semibold transition-all duration-300 ${
                  plan.featured
                    ? 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 text-white hover:scale-105 hover:shadow-xl'
                    : 'bg-white text-indigo-600 border-2 border-indigo-600 hover:bg-indigo-600 hover:text-white hover:scale-105'
                }`}
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="spinner mr-2"></div>
                    დამუშავება...
                  </div>
                ) : (
                  plan.featured ? 'დაწყება უფასო' : 'აირჩიეთ გეგმა'
                )}
              </button>
            </div>
          ))}
        </div>

        {/* FAQ Section */}
        <div className="mt-20 text-center">
          <h2 className="font-display text-3xl font-bold text-gray-900 mb-8">
            ხშირად დასმული კითხვები
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-4xl mx-auto">
            <div className="text-left">
              <h3 className="font-semibold text-gray-900 mb-2">როგორ მუშაობს გამოწერა?</h3>
              <p className="text-gray-600">
                შეგიძლიათ აირჩიოთ ყოველთვიური ან წლიური გადახდა. წლიური გეგმით დაზოგავთ 20%-ს.
              </p>
            </div>
            <div className="text-left">
              <h3 className="font-semibold text-gray-900 mb-2">შეგიძლიათ გაუქმება?</h3>
              <p className="text-gray-600">
                კი, შეგიძლიათ გაუქმდეს გამოწერა ნებისმიერ დროს. გადახდა არ აისახება შემდეგ პერიოდზე.
              </p>
            </div>
            <div className="text-left">
              <h3 className="font-semibold text-gray-900 mb-2">რა მოხდება უფასო გეგმით?</h3>
              <p className="text-gray-600">
                უფასო გეგმით შეგიძლიათ გამოიყენოთ ძირითადი ფუნქციები და შეხვდეთ მასწავლებლებს.
              </p>
            </div>
            <div className="text-left">
              <h3 className="font-semibold text-gray-900 mb-2">უსაფრთხოა გადახდა?</h3>
              <p className="text-gray-600">
                კი, ყველა გადახდა დაცულია SSL-ით და ჩვენ არ ვინახავთ თქვენს საკრედიტო ბარათის მონაცემებს.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PricingPage;
