import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useChat } from '../../contexts/ChatContext';
import { Menu, X, Bell, MessageCircle, User, LogOut, Settings } from 'lucide-react';

const Navbar = () => {
  const { currentUser, logout } = useAuth();
  const { unreadCount } = useChat();
  const navigate = useNavigate();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 glass-effect border-b border-white/20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-20">
          {/* Logo */}
          <Link to="/" className="flex items-center space-x-3 group">
            <div className="w-12 h-12 bg-gradient-to-br from-indigo-500 to-purple-600 rounded-xl flex items-center justify-center shadow-lg group-hover:shadow-xl transition-all duration-300">
              <span className="text-white font-bold text-xl">B</span>
            </div>
            <span className="font-display text-2xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
              BeMentor
            </span>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-12">
            <Link to="/" className="text-gray-700 hover:text-indigo-600 font-medium transition-colors duration-200 px-3 py-2">
              მთავარი
            </Link>
            <Link to="/explore" className="text-gray-700 hover:text-indigo-600 font-medium transition-colors duration-200 px-3 py-2">
              მასწავლებლები
            </Link>
            <Link to="/pricing" className="text-gray-700 hover:text-indigo-600 font-medium transition-colors duration-200 px-3 py-2">
              ფასები
            </Link>
            {currentUser && (
              <Link to="/chat" className="text-gray-700 hover:text-indigo-600 font-medium transition-colors duration-200 relative px-3 py-2">
                <MessageCircle className="w-5 h-5" />
                {unreadCount > 0 && (
                  <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                    {unreadCount}
                  </span>
                )}
              </Link>
            )}
          </div>

          {/* Desktop Auth Buttons */}
          <div className="hidden md:flex items-center space-x-6">
            {currentUser ? (
              <div className="flex items-center space-x-6">
                <Link to="/dashboard" className="btn-outline">
                  <User className="w-4 h-4 mr-2" />
                  პროფილი
                </Link>
                <button
                  onClick={handleLogout}
                  className="btn-primary"
                >
                  <LogOut className="w-4 h-4 mr-2" />
                  გასვლა
                </button>
              </div>
            ) : (
              <div className="flex items-center space-x-6">
                <Link to="/login" className="bg-white text-indigo-700 border-2 border-indigo-700 font-semibold py-3 px-6 rounded-xl transition-all duration-300 hover:bg-indigo-700 hover:text-white transform hover:scale-105">
                  შესვლა
                </Link>
                <Link to="/signup" className="btn-primary">
                  რეგისტრაცია
                </Link>
              </div>
            )}
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden">
            <button
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              className="text-gray-700 hover:text-indigo-600 transition-colors duration-200"
            >
              {isMobileMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isMobileMenuOpen && (
          <div className="md:hidden bg-white/95 backdrop-blur-md rounded-2xl mt-4 p-6 shadow-strong border border-gray-100">
            <div className="flex flex-col space-y-4">
              <Link
                to="/"
                className="text-gray-700 hover:text-indigo-600 font-medium transition-colors duration-200 py-2"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                მთავარი
              </Link>
              <Link
                to="/explore"
                className="text-gray-700 hover:text-indigo-600 font-medium transition-colors duration-200 py-2"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                მასწავლებლები
              </Link>
              <Link
                to="/pricing"
                className="text-gray-700 hover:text-indigo-600 font-medium transition-colors duration-200 py-2"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                ფასები
              </Link>
              {currentUser && (
                <Link
                  to="/chat"
                  className="text-gray-700 hover:text-indigo-600 font-medium transition-colors duration-200 py-2 flex items-center"
                  onClick={() => setIsMobileMenuOpen(false)}
                >
                  <MessageCircle className="w-5 h-5 mr-2" />
                  შეტყობინებები
                  {unreadCount > 0 && (
                    <span className="ml-2 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                      {unreadCount}
                    </span>
                  )}
                </Link>
              )}
              
              <div className="pt-4 border-t border-gray-200">
                {currentUser ? (
                  <div className="flex flex-col space-y-3">
                    <Link
                      to="/dashboard"
                      className="btn-outline w-full justify-center"
                      onClick={() => setIsMobileMenuOpen(false)}
                    >
                      <User className="w-4 h-4 mr-2" />
                      პროფილი
                    </Link>
                    <button
                      onClick={() => {
                        handleLogout();
                        setIsMobileMenuOpen(false);
                      }}
                      className="btn-primary w-full justify-center"
                    >
                      <LogOut className="w-4 h-4 mr-2" />
                      გასვლა
                    </button>
                  </div>
                ) : (
                  <div className="flex flex-col space-y-3">
                    <Link
                      to="/login"
                      className="btn-outline w-full justify-center"
                      onClick={() => setIsMobileMenuOpen(false)}
                    >
                      შესვლა
                    </Link>
                    <Link
                      to="/signup"
                      className="btn-primary w-full justify-center"
                      onClick={() => setIsMobileMenuOpen(false)}
                    >
                      რეგისტრაცია
                    </Link>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
