import { mockTeachers } from '../data/mockData';
import { 
  Star, 
  MapPin, 
  Clock, 
  DollarSign, 
  Award, 
  MessageCircle, 
  Calendar,
  BookOpen,
  Globe,
  Phone,
  Mail,
  ArrowLeft,
  Check,
  Users,
  Info
} from 'lucide-react';
import toast from 'react-hot-toast';

const TeacherProfilePage = () => {
