import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useChat } from '../contexts/ChatContext';
import { mockTeachers } from '../data/mockData';
import { 
  Star, 
  MapPin, 
  Clock, 
  DollarSign, 
  Award, 
  MessageCircle, 
  Calendar,
  BookOpen,
  Globe,
  Phone,
  Mail,
  ArrowLeft,
  Check,
  Users
} from 'lucide-react';
import toast from 'react-hot-toast';

const TeacherProfilePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const { createConversation } = useChat();
  const [isContactModalOpen, setIsContactModalOpen] = useState(false);
  const [isBookingModalOpen, setIsBookingModalOpen] = useState(false);

  // Find teacher by ID
  const teacher = mockTeachers.find(t => t.id === parseInt(id));

  if (!teacher) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Teacher not found</h2>
          <p className="text-gray-600 mb-4">The teacher you're looking for doesn't exist.</p>
          <button onClick={() => navigate('/explore')} className="btn-primary">
            Browse Teachers
          </button>
        </div>
      </div>
    );
  }

  const handleContact = () => {
    if (!currentUser) {
      toast.error('Please log in to contact teachers');
      navigate('/login');
      return;
    }

    if (currentUser.role === 'teacher') {
      toast.error('Teachers cannot contact other teachers');
      return;
    }

    // Create conversation and navigate to chat
    const conversationId = createConversation(
      teacher.id,
      teacher.name,
      teacher.avatar
    );
    
    navigate('/chat');
    toast.success(`Started conversation with ${teacher.name}`);
  };

  const handleBookLesson = () => {
    if (!currentUser) {
      toast.error('Please log in to book lessons');
      navigate('/login');
      return;
    }

    if (currentUser.role === 'teacher') {
      toast.error('Teachers cannot book lessons');
      return;
    }

    setIsBookingModalOpen(true);
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Back Button */}
        <button
          onClick={() => navigate('/explore')}
          className="flex items-center text-gray-600 hover:text-primary-600 mb-6 transition-colors"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Teachers
        </button>

        {/* Teacher Header */}
        <div className="card mb-8">
          <div className="flex flex-col lg:flex-row gap-8">
            {/* Teacher Image */}
            <div className="lg:w-1/3">
              <div className="relative">
                <img
                  src={teacher.avatar}
                  alt={teacher.name}
                  className="w-full h-80 lg:h-96 object-cover rounded-lg"
                />
                {teacher.isPremium && (
                  <div className="absolute top-4 right-4 bg-accent-500 text-white px-3 py-1 rounded-full text-sm font-medium flex items-center">
                    <Award className="w-4 h-4 mr-1" />
                    Premium
                  </div>
                )}
              </div>
            </div>

            {/* Teacher Info */}
            <div className="lg:w-2/3">
              <div className="flex items-start justify-between mb-4">
                <div>
                  <h1 className="text-3xl font-bold text-gray-900 mb-2">{teacher.name}</h1>
                  <p className="text-lg text-gray-600 mb-3">{teacher.education}</p>
                </div>
                <div className="text-right">
                  <div className="flex items-center text-yellow-400 mb-1">
                    {[...Array(5)].map((_, i) => (
                      <Star
                        key={i}
                        className={`w-5 h-5 ${i < Math.floor(teacher.rating) ? 'fill-current' : ''}`}
                      />
                    ))}
                  </div>
                  <p className="text-sm text-gray-600">
                    {teacher.rating} ({teacher.reviewCount} reviews)
                  </p>
                </div>
              </div>

              <div className="space-y-3 mb-6">
                <div className="flex items-center text-gray-600">
                  <MapPin className="w-4 h-4 mr-2" />
                  {teacher.location}
                </div>
                <div className="flex items-center text-gray-600">
                  <Clock className="w-4 h-4 mr-2" />
                  {teacher.experience} experience
                </div>
                <div className="flex items-center text-gray-600">
                  <DollarSign className="w-4 h-4 mr-2" />
                  ${teacher.hourlyRate}/hour
                </div>
                <div className="flex items-center text-gray-600">
                  <Globe className="w-4 h-4 mr-2" />
                  {teacher.languages.join(', ')}
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex flex-col sm:flex-row gap-3">
                <button
                  onClick={handleContact}
                  className="btn-primary flex items-center justify-center"
                >
                  <MessageCircle className="w-4 h-4 mr-2" />
                  Send Message
                </button>
                <button
                  onClick={handleBookLesson}
                  className="btn-accent flex items-center justify-center"
                >
                  <Calendar className="w-4 h-4 mr-2" />
                  Book Lesson
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Teacher Details */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-8">
            {/* About */}
            <div className="card">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">About</h2>
              <p className="text-gray-600 leading-relaxed">{teacher.bio}</p>
            </div>

            {/* Subjects */}
            <div className="card">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Subjects & Specializations</h2>
              <div className="space-y-4">
                <div>
                  <h3 className="font-medium text-gray-900 mb-2">Subjects Taught</h3>
                  <div className="flex flex-wrap gap-2">
                    {teacher.subjects.map((subject, index) => (
                      <span
                        key={index}
                        className="bg-primary-100 text-primary-700 px-3 py-1 rounded-full text-sm"
                      >
                        {subject}
                      </span>
                    ))}
                  </div>
                </div>
                <div>
                  <h3 className="font-medium text-gray-900 mb-2">Specializations</h3>
                  <div className="flex flex-wrap gap-2">
                    {teacher.specializations.map((spec, index) => (
                      <span
                        key={index}
                        className="bg-accent-100 text-accent-700 px-3 py-1 rounded-full text-sm"
                      >
                        {spec}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            </div>

            {/* Achievements */}
            <div className="card">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Achievements & Experience</h2>
              <ul className="space-y-2">
                {teacher.achievements.map((achievement, index) => (
                  <li key={index} className="flex items-start">
                    <Check className="w-5 h-5 text-green-500 mr-2 mt-0.5 flex-shrink-0" />
                    <span className="text-gray-600">{achievement}</span>
                  </li>
                ))}
              </ul>
            </div>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Availability */}
            <div className="card">
              <h3 className="font-semibold text-gray-900 mb-3">Availability</h3>
              <div className="space-y-2">
                {teacher.availability.map((day, index) => (
                  <div key={index} className="flex items-center text-gray-600">
                    <Calendar className="w-4 h-4 mr-2" />
                    {day}
                  </div>
                ))}
              </div>
            </div>

            {/* Contact Info */}
            <div className="card">
              <h3 className="font-semibold text-gray-900 mb-3">Contact Information</h3>
              <div className="space-y-2">
                <div className="flex items-center text-gray-600">
                  <Mail className="w-4 h-4 mr-2" />
                  <span className="text-sm">Available via platform</span>
                </div>
                <div className="flex items-center text-gray-600">
                  <Phone className="w-4 h-4 mr-2" />
                  <span className="text-sm">Available via platform</span>
                </div>
              </div>
            </div>

            {/* Quick Stats */}
            <div className="card">
              <h3 className="font-semibold text-gray-900 mb-3">Quick Stats</h3>
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-gray-600">Response Rate</span>
                  <span className="font-medium">98%</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-600">Response Time</span>
                  <span className="font-medium">2 hours</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-600">Students Taught</span>
                  <span className="font-medium">500+</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Booking Modal */}
      {isBookingModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Book a Lesson</h3>
            <p className="text-gray-600 mb-4">
              Booking functionality will be implemented here. For now, this is a demo.
            </p>
            <div className="flex gap-3">
              <button
                onClick={() => setIsBookingModalOpen(false)}
                className="btn-secondary flex-1"
              >
                Cancel
              </button>
              <button
                onClick={() => {
                  setIsBookingModalOpen(false);
                  toast.success('Lesson booking feature coming soon!');
                }}
                className="btn-primary flex-1"
              >
                Book Now
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherProfilePage;
