import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import { 
  Upload, 
  FileText, 
  User, 
  BookOpen, 
  MapPin, 
  Clock, 
  DollarSign, 
  Award,
  Save,
  ArrowLeft,
  X,
  Check,
  AlertCircle,
  Plus,
  Edit,
  Trash2,
  Calendar,
  GraduationCap,
  Briefcase,
  Star,
  Globe,
  Phone,
  Mail,
  Linkedin,
  Github
} from 'lucide-react';
import toast from 'react-hot-toast';

const TeacherProfileEditPage = () => {
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [cvFile, setCvFile] = useState(null);
  const [cvFileName, setCvFileName] = useState('');
  const [cvUploadProgress, setCvUploadProgress] = useState(0);
  const [activeCvTab, setActiveCvTab] = useState('upload'); // 'upload' or 'builder'
  const [profileImage, setProfileImage] = useState(null);
  const [profileImagePreview, setProfileImagePreview] = useState(currentUser?.avatar || '');

  // CV Builder state
  const [cvData, setCvData] = useState({
    personalInfo: {
      fullName: currentUser?.name || '',
      email: currentUser?.email || '',
      phone: currentUser?.phone || '',
      location: currentUser?.location || '',
      website: '',
      linkedin: '',
      github: ''
    },
    summary: currentUser?.bio || '',
    education: [
      {
        id: 1,
        degree: '',
        institution: '',
        field: '',
        startDate: '',
        endDate: '',
        gpa: '',
        description: ''
      }
    ],
    experience: [
      {
        id: 1,
        title: '',
        company: '',
        location: '',
        startDate: '',
        endDate: '',
        current: false,
        description: ''
      }
    ],
    skills: [],
    certifications: [],
    languages: currentUser?.languages || [],
    achievements: currentUser?.achievements || []
  });

  // Form state
  const [formData, setFormData] = useState({
    name: currentUser?.name || '',
    email: currentUser?.email || '',
    phone: currentUser?.phone || '',
    location: currentUser?.location || '',
    hourlyRate: currentUser?.hourlyRate || '',
    education: currentUser?.education || '',
    experience: currentUser?.experience || '',
    bio: currentUser?.bio || '',
    subjects: currentUser?.subjects || [],
    specializations: currentUser?.specializations || [],
    achievements: currentUser?.achievements || [],
    languages: currentUser?.languages || [],
    availability: currentUser?.availability || [],
    termsAndConditions: currentUser?.termsAndConditions || ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleArrayInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value.split(',').map(item => item.trim()).filter(item => item)
    }));
  };

  // CV Builder handlers
  const handleCvInputChange = (section, field, value) => {
    setCvData(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: value
      }
    }));
  };

  const handleCvArrayInputChange = (section, value) => {
    setCvData(prev => ({
      ...prev,
      [section]: value.split(',').map(item => item.trim()).filter(item => item)
    }));
  };

  const addEducation = () => {
    setCvData(prev => ({
      ...prev,
      education: [
        ...prev.education,
        {
          id: Date.now(),
          degree: '',
          institution: '',
          field: '',
          startDate: '',
          endDate: '',
          gpa: '',
          description: ''
        }
      ]
    }));
  };

  const updateEducation = (id, field, value) => {
    setCvData(prev => ({
      ...prev,
      education: prev.education.map(edu => 
        edu.id === id ? { ...edu, [field]: value } : edu
      )
    }));
  };

  const removeEducation = (id) => {
    setCvData(prev => ({
      ...prev,
      education: prev.education.filter(edu => edu.id !== id)
    }));
  };

  const addExperience = () => {
    setCvData(prev => ({
      ...prev,
      experience: [
        ...prev.experience,
        {
          id: Date.now(),
          title: '',
          company: '',
          location: '',
          startDate: '',
          endDate: '',
          current: false,
          description: ''
        }
      ]
    }));
  };

  const updateExperience = (id, field, value) => {
    setCvData(prev => ({
      ...prev,
      experience: prev.experience.map(exp => 
        exp.id === id ? { ...exp, [field]: value } : exp
      )
    }));
  };

  const removeExperience = (id) => {
    setCvData(prev => ({
      ...prev,
      experience: prev.experience.filter(exp => exp.id !== id)
    }));
  };

  const handleCvUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file type
      const allowedTypes = ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
      if (!allowedTypes.includes(file.type)) {
        toast.error('Please upload a PDF or Word document');
        return;
      }

      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        toast.error('File size must be less than 5MB');
        return;
      }

      setCvFile(file);
      setCvFileName(file.name);
      toast.success('CV uploaded successfully!');
    }
  };

  const removeCvFile = () => {
    setCvFile(null);
    setCvFileName('');
    setCvUploadProgress(0);
  };

  const handleProfileImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file type
      const allowedTypes = ['image/jpeg', 'image/png', 'image/webp'];
      if (!allowedTypes.includes(file.type)) {
        toast.error('Please upload a JPEG, PNG, or WebP image');
        return;
      }

      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        toast.error('Image size must be less than 5MB');
        return;
      }

      setProfileImage(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setProfileImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
      toast.success('Profile image uploaded successfully!');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Simulate CV upload
      if (cvFile) {
        setCvUploadProgress(0);
        for (let i = 0; i <= 100; i += 10) {
          await new Promise(resolve => setTimeout(resolve, 100));
          setCvUploadProgress(i);
        }
        toast.success('CV uploaded successfully!');
      }

      // Simulate profile update
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      toast.success('Profile updated successfully!');
      navigate('/dashboard');
    } catch (error) {
      toast.error('Error updating profile. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => navigate('/dashboard')}
                className="flex items-center text-gray-600 hover:text-primary-600 transition-colors"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Dashboard
              </button>
            </div>
            <h1 className="text-3xl font-bold text-gray-900">Edit Profile & CV</h1>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="space-y-8">
          {/* Profile Picture */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Profile Picture</h2>
            
            <div className="flex items-center space-x-6">
              <div className="relative">
                <img
                  src={profileImagePreview || "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=150&h=150&fit=crop&crop=face"}
                  alt="Profile"
                  className="w-24 h-24 rounded-full object-cover border-4 border-gray-200"
                />
                <div className="absolute -bottom-2 -right-2">
                  <label htmlFor="profile-image-upload" className="cursor-pointer">
                    <div className="w-8 h-8 bg-primary-600 rounded-full flex items-center justify-center hover:bg-primary-700 transition-colors">
                      <Upload className="w-4 h-4 text-white" />
                    </div>
                  </label>
                  <input
                    type="file"
                    id="profile-image-upload"
                    accept="image/*"
                    onChange={handleProfileImageUpload}
                    className="hidden"
                  />
                </div>
              </div>
              
              <div>
                <h3 className="font-medium text-gray-900 mb-2">Update Profile Picture</h3>
                <p className="text-sm text-gray-600 mb-3">
                  Upload a professional photo that represents you well. 
                  Recommended size: 400x400 pixels. Max file size: 5MB.
                </p>
                <div className="flex space-x-2">
                  <label htmlFor="profile-image-upload" className="btn-primary text-sm cursor-pointer">
                    Choose Photo
                  </label>
                  {profileImagePreview && profileImagePreview !== currentUser?.avatar && (
                    <button
                      type="button"
                      onClick={() => {
                        setProfileImage(null);
                        setProfileImagePreview(currentUser?.avatar || '');
                      }}
                      className="btn-secondary text-sm"
                    >
                      Reset
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* CV Section */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">CV/Resume</h2>
            
            {/* CV Tabs */}
            <div className="mb-6">
              <div className="flex space-x-1 bg-gray-100 p-1 rounded-lg">
                <button
                  type="button"
                  onClick={() => setActiveCvTab('upload')}
                  className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
                    activeCvTab === 'upload'
                      ? 'bg-white text-primary-600 shadow-sm'
                      : 'text-gray-600 hover:text-gray-900'
                  }`}
                >
                  <Upload className="w-4 h-4 inline mr-2" />
                  Upload CV
                </button>
                <button
                  type="button"
                  onClick={() => setActiveCvTab('builder')}
                  className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
                    activeCvTab === 'builder'
                      ? 'bg-white text-primary-600 shadow-sm'
                      : 'text-gray-600 hover:text-gray-900'
                  }`}
                >
                  <Edit className="w-4 h-4 inline mr-2" />
                  CV Builder
                </button>
              </div>
            </div>

            {activeCvTab === 'upload' && (
              <div className="space-y-4">
                <div className="flex items-center space-x-4">
                  <div className="flex-1">
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Upload your CV/Resume
                    </label>
                    <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-primary-400 transition-colors">
                      <input
                        type="file"
                        accept=".pdf,.doc,.docx"
                        onChange={handleCvUpload}
                        className="hidden"
                        id="cv-upload"
                      />
                      <label htmlFor="cv-upload" className="cursor-pointer">
                        <Upload className="w-8 h-8 text-gray-400 mx-auto mb-2" />
                        <p className="text-sm text-gray-600">
                          Click to upload or drag and drop
                        </p>
                        <p className="text-xs text-gray-500 mt-1">
                          PDF, DOC, DOCX (max 5MB)
                        </p>
                      </label>
                    </div>
                  </div>
                </div>

                {cvFile && (
                  <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <FileText className="w-5 h-5 text-green-600" />
                        <div>
                          <p className="font-medium text-green-900">{cvFileName}</p>
                          <p className="text-sm text-green-700">
                            {(cvFile.size / 1024 / 1024).toFixed(2)} MB
                          </p>
                        </div>
                      </div>
                      <button
                        type="button"
                        onClick={removeCvFile}
                        className="text-red-600 hover:text-red-700"
                      >
                        <X className="w-5 h-5" />
                      </button>
                    </div>
                    
                    {cvUploadProgress > 0 && cvUploadProgress < 100 && (
                      <div className="mt-3">
                        <div className="flex items-center justify-between text-sm text-green-700 mb-1">
                          <span>Uploading...</span>
                          <span>{cvUploadProgress}%</span>
                        </div>
                        <div className="w-full bg-green-200 rounded-full h-2">
                          <div 
                            className="bg-green-600 h-2 rounded-full transition-all duration-300"
                            style={{ width: `${cvUploadProgress}%` }}
                          ></div>
                        </div>
                      </div>
                    )}
                    
                    {cvUploadProgress === 100 && (
                      <div className="flex items-center mt-3 text-green-700">
                        <Check className="w-4 h-4 mr-2" />
                        <span className="text-sm">Upload complete!</span>
                      </div>
                    )}
                  </div>
                )}

                <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                  <div className="flex items-start">
                    <AlertCircle className="w-5 h-5 text-blue-600 mt-0.5 mr-3 flex-shrink-0" />
                    <div>
                      <h3 className="font-medium text-blue-900 mb-1">CV Upload Tips</h3>
                      <ul className="text-sm text-blue-800 space-y-1">
                        <li>• Include your education, experience, and certifications</li>
                        <li>• Highlight your teaching experience and achievements</li>
                        <li>• Keep it professional and well-formatted</li>
                        <li>• Maximum file size: 5MB</li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {activeCvTab === 'builder' && (
              <div className="space-y-6">
                {/* Personal Information */}
                <div className="bg-gray-50 rounded-lg p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                    <User className="w-5 h-5 mr-2" />
                    Personal Information
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Full Name</label>
                      <input
                        type="text"
                        value={cvData.personalInfo.fullName}
                        onChange={(e) => handleCvInputChange('personalInfo', 'fullName', e.target.value)}
                        className="input-field"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                      <input
                        type="email"
                        value={cvData.personalInfo.email}
                        onChange={(e) => handleCvInputChange('personalInfo', 'email', e.target.value)}
                        className="input-field"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Phone</label>
                      <input
                        type="tel"
                        value={cvData.personalInfo.phone}
                        onChange={(e) => handleCvInputChange('personalInfo', 'phone', e.target.value)}
                        className="input-field"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Location</label>
                      <input
                        type="text"
                        value={cvData.personalInfo.location}
                        onChange={(e) => handleCvInputChange('personalInfo', 'location', e.target.value)}
                        className="input-field"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">Website</label>
                      <input
                        type="url"
                        value={cvData.personalInfo.website}
                        onChange={(e) => handleCvInputChange('personalInfo', 'website', e.target.value)}
                        className="input-field"
                        placeholder="https://yourwebsite.com"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">LinkedIn</label>
                      <input
                        type="url"
                        value={cvData.personalInfo.linkedin}
                        onChange={(e) => handleCvInputChange('personalInfo', 'linkedin', e.target.value)}
                        className="input-field"
                        placeholder="https://linkedin.com/in/yourprofile"
                      />
                    </div>
                  </div>
                </div>

                {/* Professional Summary */}
                <div className="bg-gray-50 rounded-lg p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                    <FileText className="w-5 h-5 mr-2" />
                    Professional Summary
                  </h3>
                  <textarea
                    value={cvData.summary}
                    onChange={(e) => setCvData(prev => ({ ...prev, summary: e.target.value }))}
                    rows={4}
                    className="input-field"
                    placeholder="Write a compelling summary of your teaching experience, expertise, and what makes you unique..."
                  />
                </div>

                {/* Education */}
                <div className="bg-gray-50 rounded-lg p-6">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                      <GraduationCap className="w-5 h-5 mr-2" />
                      Education
                    </h3>
                    <button
                      type="button"
                      onClick={addEducation}
                      className="btn-primary text-sm flex items-center"
                    >
                      <Plus className="w-4 h-4 mr-1" />
                      Add Education
                    </button>
                  </div>
                  <div className="space-y-4">
                    {cvData.education.map((edu, index) => (
                      <div key={edu.id} className="border border-gray-200 rounded-lg p-4">
                        <div className="flex items-center justify-between mb-3">
                          <h4 className="font-medium text-gray-900">Education #{index + 1}</h4>
                          {cvData.education.length > 1 && (
                            <button
                              type="button"
                              onClick={() => removeEducation(edu.id)}
                              className="text-red-600 hover:text-red-700"
                            >
                              <Trash2 className="w-4 h-4" />
                            </button>
                          )}
                        </div>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">Degree</label>
                            <input
                              type="text"
                              value={edu.degree}
                              onChange={(e) => updateEducation(edu.id, 'degree', e.target.value)}
                              className="input-field"
                              placeholder="e.g., PhD in Mathematics"
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">Institution</label>
                            <input
                              type="text"
                              value={edu.institution}
                              onChange={(e) => updateEducation(edu.id, 'institution', e.target.value)}
                              className="input-field"
                              placeholder="e.g., Tbilisi State University"
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">Field of Study</label>
                            <input
                              type="text"
                              value={edu.field}
                              onChange={(e) => updateEducation(edu.id, 'field', e.target.value)}
                              className="input-field"
                              placeholder="e.g., Mathematics"
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">GPA</label>
                            <input
                              type="text"
                              value={edu.gpa}
                              onChange={(e) => updateEducation(edu.id, 'gpa', e.target.value)}
                              className="input-field"
                              placeholder="e.g., 3.8/4.0"
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">Start Date</label>
                            <input
                              type="month"
                              value={edu.startDate}
                              onChange={(e) => updateEducation(edu.id, 'startDate', e.target.value)}
                              className="input-field"
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">End Date</label>
                            <input
                              type="month"
                              value={edu.endDate}
                              onChange={(e) => updateEducation(edu.id, 'endDate', e.target.value)}
                              className="input-field"
                            />
                          </div>
                        </div>
                        <div className="mt-4">
                          <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                          <textarea
                            value={edu.description}
                            onChange={(e) => updateEducation(edu.id, 'description', e.target.value)}
                            rows={3}
                            className="input-field"
                            placeholder="Describe your studies, research, achievements..."
                          />
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Experience */}
                <div className="bg-gray-50 rounded-lg p-6">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                      <Briefcase className="w-5 h-5 mr-2" />
                      Work Experience
                    </h3>
                    <button
                      type="button"
                      onClick={addExperience}
                      className="btn-primary text-sm flex items-center"
                    >
                      <Plus className="w-4 h-4 mr-1" />
                      Add Experience
                    </button>
                  </div>
                  <div className="space-y-4">
                    {cvData.experience.map((exp, index) => (
                      <div key={exp.id} className="border border-gray-200 rounded-lg p-4">
                        <div className="flex items-center justify-between mb-3">
                          <h4 className="font-medium text-gray-900">Experience #{index + 1}</h4>
                          {cvData.experience.length > 1 && (
                            <button
                              type="button"
                              onClick={() => removeExperience(exp.id)}
                              className="text-red-600 hover:text-red-700"
                            >
                              <Trash2 className="w-4 h-4" />
                            </button>
                          )}
                        </div>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">Job Title</label>
                            <input
                              type="text"
                              value={exp.title}
                              onChange={(e) => updateExperience(exp.id, 'title', e.target.value)}
                              className="input-field"
                              placeholder="e.g., Mathematics Teacher"
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">Company</label>
                            <input
                              type="text"
                              value={exp.company}
                              onChange={(e) => updateExperience(exp.id, 'company', e.target.value)}
                              className="input-field"
                              placeholder="e.g., Tbilisi State University"
                            />
                          </div>
                          <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">Location</label>
                            <input
                              type="text"
                              value={exp.location}
                              onChange={(e) => updateExperience(exp.id, 'location', e.target.value)}
                              className="input-field"
                              placeholder="e.g., Tbilisi, Georgia"
                            />
                          </div>
                          <div className="flex items-center space-x-4">
                            <div className="flex-1">
                              <label className="block text-sm font-medium text-gray-700 mb-1">Start Date</label>
                              <input
                                type="month"
                                value={exp.startDate}
                                onChange={(e) => updateExperience(exp.id, 'startDate', e.target.value)}
                                className="input-field"
                              />
                            </div>
                            <div className="flex-1">
                              <label className="block text-sm font-medium text-gray-700 mb-1">End Date</label>
                              <input
                                type="month"
                                value={exp.endDate}
                                onChange={(e) => updateExperience(exp.id, 'endDate', e.target.value)}
                                className="input-field"
                                disabled={exp.current}
                              />
                            </div>
                          </div>
                        </div>
                        <div className="mt-4 flex items-center">
                          <input
                            type="checkbox"
                            id={`current-${exp.id}`}
                            checked={exp.current}
                            onChange={(e) => updateExperience(exp.id, 'current', e.target.checked)}
                            className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                          />
                          <label htmlFor={`current-${exp.id}`} className="ml-2 text-sm text-gray-700">
                            I currently work here
                          </label>
                        </div>
                        <div className="mt-4">
                          <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                          <textarea
                            value={exp.description}
                            onChange={(e) => updateExperience(exp.id, 'description', e.target.value)}
                            rows={4}
                            className="input-field"
                            placeholder="Describe your responsibilities, achievements, and impact..."
                          />
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Skills */}
                <div className="bg-gray-50 rounded-lg p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                    <Star className="w-5 h-5 mr-2" />
                    Skills
                  </h3>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Skills (comma-separated)
                    </label>
                    <input
                      type="text"
                      value={cvData.skills.join(', ')}
                      onChange={(e) => handleCvArrayInputChange('skills', e.target.value)}
                      className="input-field"
                      placeholder="e.g., Mathematics, Physics, Calculus, Test Preparation, Online Teaching"
                    />
                  </div>
                </div>

                {/* Languages */}
                <div className="bg-gray-50 rounded-lg p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                    <Globe className="w-5 h-5 mr-2" />
                    Languages
                  </h3>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Languages (comma-separated)
                    </label>
                    <input
                      type="text"
                      value={cvData.languages.join(', ')}
                      onChange={(e) => handleCvArrayInputChange('languages', e.target.value)}
                      className="input-field"
                      placeholder="e.g., Georgian (Native), English (Fluent), Russian (Intermediate)"
                    />
                  </div>
                </div>

                {/* Certifications */}
                <div className="bg-gray-50 rounded-lg p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                    <Award className="w-5 h-5 mr-2" />
                    Certifications & Achievements
                  </h3>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Certifications (comma-separated)
                    </label>
                    <input
                      type="text"
                      value={cvData.certifications.join(', ')}
                      onChange={(e) => handleCvArrayInputChange('certifications', e.target.value)}
                      className="input-field"
                      placeholder="e.g., Teaching Certificate, Math Olympiad Coach, Online Teaching Certification"
                    />
                  </div>
                  <div className="mt-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Achievements (comma-separated)
                    </label>
                    <input
                      type="text"
                      value={cvData.achievements.join(', ')}
                      onChange={(e) => handleCvArrayInputChange('achievements', e.target.value)}
                      className="input-field"
                      placeholder="e.g., Teacher of the Year 2022, Published 15+ papers, 10,000+ hours taught"
                    />
                  </div>
                </div>
              </div>
            )}
          </div>

                    {/* Basic Information */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Basic Information</h2>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Full Name
                </label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="input-field"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Email
                </label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="input-field"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Phone Number
                </label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  className="input-field"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Location
                </label>
                <input
                  type="text"
                  name="location"
                  value={formData.location}
                  onChange={handleInputChange}
                  className="input-field"
                  placeholder="e.g., Tbilisi, Georgia"
                />
              </div>
            </div>
          </div>

          {/* Pricing & Availability */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Pricing & Availability</h2>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Hourly Rate (₾)
                </label>
                <div className="relative">
                  <input
                    type="number"
                    name="hourlyRate"
                    value={formData.hourlyRate}
                    onChange={handleInputChange}
                    className="input-field pl-8"
                    placeholder="e.g., 45"
                    min="0"
                  />
                  <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500">₾</span>
                </div>
                <p className="text-xs text-gray-500 mt-1">Set your hourly rate for lessons</p>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Years of Experience
                </label>
                <input
                  type="text"
                  name="experience"
                  value={formData.experience}
                  onChange={handleInputChange}
                  className="input-field"
                  placeholder="e.g., 5 years"
                />
              </div>

              <div className="md:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Availability (comma-separated)
                </label>
                <input
                  type="text"
                  value={formData.availability.join(', ')}
                  onChange={(e) => handleArrayInputChange('availability', e.target.value)}
                  className="input-field"
                  placeholder="e.g., Monday, Wednesday, Friday, Saturday"
                />
                <p className="text-xs text-gray-500 mt-1">List the days you're available for lessons</p>
              </div>
            </div>
          </div>

          {/* Education & Bio */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Education & Bio</h2>
            
            <div className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Education
                </label>
                <textarea
                  name="education"
                  value={formData.education}
                  onChange={handleInputChange}
                  rows={3}
                  className="input-field"
                  placeholder="e.g., PhD in Mathematics, TSU"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Bio
                </label>
                <textarea
                  name="bio"
                  value={formData.bio}
                  onChange={handleInputChange}
                  rows={4}
                  className="input-field"
                  placeholder="Tell students about your teaching style, experience, and what makes you unique..."
                />
              </div>
            </div>
          </div>

          {/* Subjects & Specializations */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Subjects & Specializations</h2>
            
            <div className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Subjects (comma-separated)
                </label>
                <input
                  type="text"
                  value={formData.subjects.join(', ')}
                  onChange={(e) => handleArrayInputChange('subjects', e.target.value)}
                  className="input-field"
                  placeholder="e.g., Mathematics, Physics, Calculus"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Specializations (comma-separated)
                </label>
                <input
                  type="text"
                  value={formData.specializations.join(', ')}
                  onChange={(e) => handleArrayInputChange('specializations', e.target.value)}
                  className="input-field"
                  placeholder="e.g., AP Calculus, Test Prep, College Math"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Languages (comma-separated)
                </label>
                <input
                  type="text"
                  value={formData.languages.join(', ')}
                  onChange={(e) => handleArrayInputChange('languages', e.target.value)}
                  className="input-field"
                  placeholder="e.g., Georgian, English, Russian"
                />
              </div>
            </div>
          </div>

          {/* Achievements */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 mb-6">Achievements & Certifications</h2>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Achievements (comma-separated)
              </label>
              <textarea
                value={formData.achievements.join(', ')}
                onChange={(e) => handleArrayInputChange('achievements', e.target.value)}
                rows={3}
                className="input-field"
                placeholder="e.g., Teacher of the Year 2022, Published 15+ papers, 10,000+ hours taught"
              />
            </div>
          </div>

          {/* Submit Button */}
          <div className="flex justify-end space-x-4">
            <button
              type="button"
              onClick={() => navigate('/dashboard')}
              className="btn-secondary"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isLoading}
              className="btn-primary flex items-center"
            >
              {isLoading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Saving...
                </>
              ) : (
                <>
                  <Save className="w-4 h-4 mr-2" />
                  Save Changes
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TeacherProfileEditPage;
