import React, { createContext, useContext, useState, useEffect } from 'react';
import toast from 'react-hot-toast';
import api from '../services/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is logged in on app start
    const token = api.getToken();
    if (token) {
      loadCurrentUser();
    } else {
      setLoading(false);
    }
  }, []);

  const loadCurrentUser = async () => {
    try {
      const response = await api.getCurrentUser();
      setCurrentUser(response.user);
    } catch (error) {
      console.error('Failed to load current user:', error);
      api.removeToken();
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password, role) => {
    try {
      // Mock API call - replace with actual backend
      const mockUsers = [
        {
          id: 1,
          email: 'student@example.com',
          password: 'password123',
          role: 'student',
          name: 'John Student',
          avatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=150&h=150&fit=crop&crop=face'
        },
        {
          id: 2,
          email: 'teacher@example.com',
          password: 'password123',
          role: 'teacher',
          name: 'Dr. Sarah Johnson',
          avatar: 'https://images.unsplash.com/photo-1494790108755-2616b612b786?w=150&h=150&fit=crop&crop=face'
        }
      ];

      const user = mockUsers.find(u => u.email === email && u.password === password && u.role === role);
      
      if (!user) {
        throw new Error('Invalid credentials');
      }

      // Mock JWT token
      const token = btoa(JSON.stringify({
        id: user.id,
        email: user.email,
        role: user.role,
        name: user.name,
        avatar: user.avatar,
        exp: Date.now() + 24 * 60 * 60 * 1000 // 24 hours
      }));

      localStorage.setItem('token', token);
      setCurrentUser({
        id: user.id,
        email: user.email,
        role: user.role,
        name: user.name,
        avatar: user.avatar
      });

      toast.success(`Welcome back, ${user.name}!`);
      return true;
    } catch (error) {
      toast.error(error.message || 'Login failed');
      return false;
    }
  };

  const signup = async (userData) => {
    try {
      // Mock API call - replace with actual backend
      const newUser = {
        id: Date.now(),
        ...userData,
        avatar: `https://images.unsplash.com/photo-${Math.floor(Math.random() * 1000000)}?w=150&h=150&fit=crop&crop=face`
      };

      // Mock JWT token
      const token = btoa(JSON.stringify({
        id: newUser.id,
        email: newUser.email,
        role: newUser.role,
        name: newUser.name,
        avatar: newUser.avatar,
        exp: Date.now() + 24 * 60 * 60 * 1000 // 24 hours
      }));

      localStorage.setItem('token', token);
      setCurrentUser({
        id: newUser.id,
        email: newUser.email,
        role: newUser.role,
        name: newUser.name,
        avatar: newUser.avatar
      });

      toast.success('Account created successfully!');
      return true;
    } catch (error) {
      toast.error(error.message || 'Signup failed');
      return false;
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setCurrentUser(null);
    toast.success('Logged out successfully');
  };

  const value = {
    currentUser,
    login,
    signup,
    logout,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
